def contains(text, pattern):
    l = len(pattern)
    for i in range(len(text)):
        if text[i:i+1] == pattern:
            return True
    return False

def unescape(s):
    ss = ""
    flag = False
    for c in s:
        if flag and c == 'n':
            ss += '\n'
        elif flag and c == 't':
            ss += '\t'
        elif flag and c == '\\':
            ss += '\\'
        elif c == '\\':
            flag = True
        else:
            ss += c
    return ss

def is_discount_hunter(customer):
    flag = True
    for order in customer.orders:
        flag2 = False
        for item in order.items:
            if item.product.discount > 0:
                flag2 = True
        flag = flag and flag2
    return flag

def GetBigramsFrequency(ws):
    bigramsCount = len(ws) - 1
    bigramsfrequency = {}
    for I in range(bigramsCount):
        bg = ws[I] + ' ' + ws[I + 1]
        if bg in bigramsfrequency:
            bigramsfrequency[bg] += 1
        else:
            bigramsfrequency[bg] = 1
    return bigramsfrequency

def InstructionParser(i):
    Instructions = []
    F = False
    for l in i.splitlines():
        if l.startswith("BEGIN"):
            F = True
        if not F:
            continue
        if l.startswith("END"):
            F = False
        elif l.startswith("replace "):
            rest = l[8:]
            old, new = rest.split(" with ")
            Instructions.append(("replace", old, new))
        elif l.startswith("add "):
            rest = l[4:]
            Instructions.append(("add", rest))    
        else:
            raise Exception("Unknown instruction in line: " + l)
    return Instructions

